(596, 'poll_template_votefooter', '</ul><p style=\"text-align: center\"><input type=\"button\" name=\"vote\" value=\"   Vote   \" class=\"Buttons\" onclick=\"poll_vote(%POLL_ID%);\" /></p><p style=\"text-align: center\"><a href=\"#ViewPollResults\" onclick=\"poll_result(%POLL_ID%); return false;\" title=\"View Results Of This Poll\">Ver resultados aquí</a></div>', 'yes'),
(597, 'poll_template_resultheader', '<p style=\"text-align: center\"><strong>%POLL_QUESTION%</strong></p><div id=\"polls-%POLL_ID%-ans\" class=\"wp-polls-ans\"><ul class=\"wp-polls-ul\">', 'yes'),
(598, 'poll_template_resultbody', '<li>%POLL_ANSWER% <small>(%POLL_ANSWER_PERCENTAGE%%, %POLL_ANSWER_VOTES% Votes)</small><div class=\"pollbar\" style=\"width: %POLL_ANSWER_IMAGEWIDTH%%\" title=\"%POLL_ANSWER_TEXT% (%POLL_ANSWER_PERCENTAGE%% | %POLL_ANSWER_VOTES% Votes)\"></div></li>', 'yes'),
(599, 'poll_template_resultbody2', '<li><strong><i>%POLL_ANSWER% <small>(%POLL_ANSWER_PERCENTAGE%%, %POLL_ANSWER_VOTES% Votes)</small></i></strong><div class=\"pollbar\" style=\"width: %POLL_ANSWER_IMAGEWIDTH%%\" title=\"You Have Voted For This Choice - %POLL_ANSWER_TEXT% (%POLL_ANSWER_PERCENTAGE%% | %POLL_ANSWER_VOTES% Votes)\"></div></li>', 'yes'),
(600, 'poll_template_resultfooter', '</ul><p style=\"text-align: center\">Encuestados: <strong>%POLL_TOTALVOTERS%</strong> personas</p></div>', 'yes'),
(601, 'poll_template_resultfooter2', '</ul><p style=\"text-align: center\">Encuestados: <strong>%POLL_TOTALVOTERS%</strong> personas</p><p style=\"text-align: center\"><a href=\"#VotePoll\" onclick=\"poll_booth(%POLL_ID%); return false;\" title=\"Vote For This Poll\">Vote</a></p></div>', 'yes'),
